<script>
  let left=0;
  export default {
    name: "Middle",
    methods:{
      goleft(){
        let  brick = document.querySelector(".sliderbox_core_box1");
        if(parseInt(brick.style.left) <= -810){
          return
        }
        else{
          left-=270;
          brick.style.left=left+'px';
        }
      },
      goright(){
        let  brick = document.querySelector(".sliderbox_core_box1");
        if(parseInt(brick.style.left) >= 0){
          return
        }
        else{
          left+=270;
          brick.style.left=left+'px';
        }
      },
      moveleft(){
        let  distance = document.querySelector(".herobox");
        if(parseInt(distance.style.left) < -1000){
          return
        }
        else{
          left-=353;
          distance.style.left=left+'px';
        }
      },
      moveright(){
        let  distance = document.querySelector(".herobox");
        if(parseInt(distance.style.left) >= -20){
          return
        }
        else{
          left+=353;
          distance.style.left=left+'px';
        }
      }
    },
    mounted() {
      //点击top返回顶部
      $(window).scroll(function(){
        if(document.body.scrollTop>=1000||document.documentElement.scrollTop>=1000){
          $('.gotop').css({"display":"block"})
        }
        else{
          $('.gotop').css({"display":"none"})
        }

        $('.gotop').click(function(){
          var timer=setInterval(function(){
            if($(document).scrollTop()==0){
              clearInterval(timer);
            }else{
              $(document).scrollTop($(document).scrollTop()-100);
            }
          },50);
        });

      })
    }
  }

